mapper.select:
SELECT p.ParticipantID AS imsId, c.ISO2 AS country, n.Name_Full AS title, p.Name_Full AS participantTitle, p.Name_Short AS abbreviation, n.URL AS homepage, p.Name_Full AS participantTitle, p.Member_AsOf AS participantSince, p.PublicDescription AS description, null AS modified, i.Name AS institution, i.Address AS address, i.City AS city, i.ZipCode AS postalCode, i.State AS province, i.Telephone AS phone, i.Email AS email, p.GBIFmembership, null AS Region, p.MOU2012_Date AS MOU2012_Date
FROM gbifims_Node n JOIN gbifims_Participant p ON n.ParticipantID = p.ParticipantID LEFT JOIN gbifims_Country c ON p.CountryID = c.CountryID LEFT JOIN gbifims_Institution i ON p.InstitutionID = i.InstitutionID
WHERE p.ParticipantID = #{imsId}
